import React, { useState, useEffect, useRef } from "react";
import mqtt from "mqtt";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,  
  Legend,
} from "chart.js";   

import "chart.js/auto";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const MQTT_BROKER = "ws://localhost:9001"; // Replace with your broker address
const MQTT_TOPIC_MOTION = "home/security/motion";
const MQTT_TOPIC_DOOR = "home/security/door";
const MQTT_TOPIC_WINDOW = "home/security/window";
const MQTT_TOPIC_TEMPERATURE = "home/sensors/temperature";
const MQTT_TOPIC_CAMERA_IMAGE = "home/camera/door/image";
const MQTT_TOPIC_CAMERA_MOTION = "home/camera/door/motion";

function App() {
  const [motionDetected, setMotionDetected] = useState(false);
  const [doorOpen, setDoorOpen] = useState(false);
  const [windowOpen, setWindowOpen] = useState(false);
  const [temperature, setTemperature] = useState(20);
  const [temperatureData, setTemperatureData] = useState([]);
  const [cameraImage, setCameraImage] = useState(null);
  const [showImage, setShowImage] = useState(false);
  const timeoutId = useRef(null);

  useEffect(() => {
    const client = mqtt.connect(MQTT_BROKER);

    client.on("connect", () => {
      console.log("Connected to MQTT Broker!");
      client.subscribe(MQTT_TOPIC_MOTION);
      client.subscribe(MQTT_TOPIC_DOOR);
      client.subscribe(MQTT_TOPIC_WINDOW);
      client.subscribe(MQTT_TOPIC_TEMPERATURE);
      client.subscribe(MQTT_TOPIC_CAMERA_IMAGE);
      client.subscribe(MQTT_TOPIC_CAMERA_MOTION);
    });

    client.on("message", (topic, message) => {
      if (topic === MQTT_TOPIC_MOTION) {
        setMotionDetected(JSON.parse(message.toString()).motion_detected);
      } else if (topic === MQTT_TOPIC_DOOR) {
        setDoorOpen(JSON.parse(message.toString()).door_open);
      } else if (topic === MQTT_TOPIC_WINDOW) {
        setWindowOpen(JSON.parse(message.toString()).window_open);
      } else if (topic === MQTT_TOPIC_TEMPERATURE) {
        const tempData = JSON.parse(message.toString());
        setTemperature(tempData.temperature);
        setTemperatureData((prevData) => {
          const newData = [...prevData, tempData.temperature];
          if (newData.length > 10) {
            newData.shift();
          }
          return newData;
        });
      } else if (topic === MQTT_TOPIC_CAMERA_IMAGE) {
        setCameraImage(message.toString());
        setShowImage(true); // Show the image immediately when it arrives

        // Clear existing timeout to avoid premature hiding
        if (timeoutId.current) {
          clearTimeout(timeoutId.current);
        }

        // Set timeout to hide the image after 5 seconds
        timeoutId.current = setTimeout(() => {
          setShowImage(false);
        }, 5000);
      }
    });

    // Clean up on unmount
    return () => {
      if (client.connected) {
        client.end();
      }
    };
  }, []);

  const chartData = {
    labels: Array.from({ length: temperatureData.length }, (_, i) => i + 1),
    datasets: [
      {
        label: "Temperature (°C)",
        data: temperatureData,
        fill: false,
        borderColor: "rgb(75, 192, 192)",
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="App p-8 bg-gradient-to-r from-blue-200 to-purple-200 min-h-screen">
      <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">
        Smart Home Dashboard
      </h1>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Sensor Data */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-bold mb-4 text-gray-800 text-center">Sensors</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            {/* Motion Sensor */}
            <div className="flex flex-col items-center bg-gradient-to-b from-white to-gray-100 p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300">
              <div className="flex items-center space-x-3 mb-4">
                <span className="text-gray-700 text-lg font-semibold">Motion Sensor</span>
                <div
                    className={`w-4 h-4 rounded-full ${
                        motionDetected ? "bg-red-500 animate-ping" : "bg-green-500"
                    }`}
                    title={motionDetected ? "Motion detected!" : "No motion detected"}
                ></div>
              </div>
              <div
                  className={`w-16 h-16 flex items-center justify-center rounded-full border-4 ${
                      motionDetected ? "border-red-500 bg-red-100" : "border-green-500 bg-green-100"
                  }`}
              >
                {motionDetected ? (
                    <i className="text-red-500 text-2xl fas fa-exclamation-circle"></i>
                ) : (
                    <i className="text-green-500 text-2xl fas fa-check-circle"></i>
                )}
              </div>
              <p
                  className={`mt-4 text-lg font-medium ${
                      motionDetected ? "text-red-600" : "text-green-600"
                  }`}
              >
                {motionDetected ? "Motion Detected" : "No Motion"}
              </p>
            </div>


            {/* Door Sensor */}
            <div className="flex flex-col items-center bg-gray-100 p-4 rounded-lg shadow">
              <h3 className="text-gray-700 font-bold mb-2">Door</h3>
              <div
                  className={`w-12 h-12 flex items-center justify-center rounded-full ${
                      doorOpen ? "bg-red-500" : "bg-green-500"
                  }`}
              >
        <span className="text-white font-bold">
          {doorOpen ? "Open" : "Closed"}
        </span>
              </div>
            </div>

            {/* Window Sensor */}
            <div className="flex flex-col items-center bg-gray-100 p-4 rounded-lg shadow">
              <h3 className="text-gray-700 font-bold mb-2">Window</h3>
              <div
                  className={`w-12 h-12 flex items-center justify-center rounded-full ${
                      windowOpen ? "bg-red-500" : "bg-green-500"
                  }`}
              >
        <span className="text-white font-bold">
          {windowOpen ? "Open" : "Closed"}
        </span>
              </div>
            </div>

            {/* Temperature Sensor */}
            <div className="flex flex-col items-center bg-gray-100 p-4 rounded-lg shadow">
              <h3 className="text-gray-700 font-bold mb-2">Temperature</h3>
              <div className="text-xl font-bold text-gray-800">
                {temperature}°C
              </div>
            </div>
          </div>
        </div>



        {/* Temperature Chart */}
        <div className="bg-white p-6 rounded-lg shadow-md col-span-2 lg:col-span-1">
          <h2 className="text-xl font-bold mb-4 text-gray-800">
            Temperature Chart
          </h2>
          <Line data={chartData} />
        </div>

        {/* Camera Image */}
        <div className="bg-white p-6 rounded-lg shadow-md col-span-2 lg:col-span-1">
          <h2 className="text-xl font-bold mb-4 text-gray-800">Door Camera</h2>
          {cameraImage && showImage ? ( // Use showImage here to control visibility
            <img
              src={`data:image/jpeg;base64,${cameraImage}`}
              alt="Door Camera"
              className="border-2 border-gray-300 rounded-lg shadow-md max-w-xs mx-auto"
            />
          ) : (
            <p className="text-gray-600 text-center">No image available</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
