import './App.css';
import React, { useState, useEffect, useRef } from "react";
import mqtt from "mqtt";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

import "chart.js/auto";

// Register Chart.js components
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

// MQTT Broker and Topics
const MQTT_BROKER = "ws://localhost:9001"; // Replace with your broker address
const MQTT_TOPIC_MOTION = "home/security/motion";
const MQTT_TOPIC_DOOR = "home/security/door";
const MQTT_TOPIC_WINDOW = "home/security/window";
const MQTT_TOPIC_TEMPERATURE = "home/sensors/temperature";
const MQTT_TOPIC_CAMERA_IMAGE = "home/camera/door/image";
const MQTT_TOPIC_CAMERA_MOTION = "home/camera/door/motion";

function App() {
  // State hooks for managing various sensor and camera data
  const [motionDetected, setMotionDetected] = useState(false);
  const [doorOpen, setDoorOpen] = useState(false);
  const [windowOpen, setWindowOpen] = useState(false);
  const [temperature, setTemperature] = useState(20);
  const [temperatureData, setTemperatureData] = useState([]);
  const [cameraImage, setCameraImage] = useState(null);
  const [showImage, setShowImage] = useState(false);
  const timeoutId = useRef(null); // For controlling the timer to hide the image

  // Effect hook to establish MQTT connection and handle messages
  useEffect(() => {
    const client = mqtt.connect(MQTT_BROKER);

    client.on("connect", () => {
      console.log("Connected to MQTT Broker!");
      // Subscribe to all relevant topics
      client.subscribe(MQTT_TOPIC_MOTION);
      client.subscribe(MQTT_TOPIC_DOOR);
      client.subscribe(MQTT_TOPIC_WINDOW);
      client.subscribe(MQTT_TOPIC_TEMPERATURE);
      client.subscribe(MQTT_TOPIC_CAMERA_IMAGE);
      client.subscribe(MQTT_TOPIC_CAMERA_MOTION);
    });

    client.on("message", (topic, message) => {
      if (topic === MQTT_TOPIC_MOTION) {
        setMotionDetected(JSON.parse(message.toString()).motion_detected);
      } else if (topic === MQTT_TOPIC_DOOR) {
        setDoorOpen(JSON.parse(message.toString()).door_open);
      } else if (topic === MQTT_TOPIC_WINDOW) {
        setWindowOpen(JSON.parse(message.toString()).window_open);
      } else if (topic === MQTT_TOPIC_TEMPERATURE) {
        const tempData = JSON.parse(message.toString());
        setTemperature(tempData.temperature);

        // Update temperature data (only store the last 10 data points)
        setTemperatureData((prevData) => {
          const newData = [...prevData, tempData.temperature];
          if (newData.length > 10) {
            newData.shift(); // Remove the oldest data point if there are more than 10
          }
          return newData;
        });
      } else if (topic === MQTT_TOPIC_CAMERA_IMAGE) {
        setCameraImage(message.toString());
        setShowImage(true); // Show the image immediately when it arrives

        // Clear existing timeout to avoid premature hiding
        if (timeoutId.current) {
          clearTimeout(timeoutId.current);
        }

        // Set a timeout to hide the image after 5 seconds
        timeoutId.current = setTimeout(() => {
          setShowImage(false);
        }, 5000);
      }
    });

    // Cleanup when the component unmounts
    return () => {
      if (client.connected) {
        client.end();
      }
    };
  }, []);

  // Prepare the data for the temperature chart
  const chartData = {
    labels: Array.from({ length: temperatureData.length }, (_, i) => i + 1),
    datasets: [
      {
        label: "Temperature (°C)",
        data: temperatureData,
        fill: false,
        borderColor: "rgb(75, 192, 192)",
        tension: 0.1,
      },
    ],
  };

  return (
      <div className="App p-8 bg-gradient-to-r from-blue-200 to-purple-200 min-h-screen">
        {/* Main title of the dashboard */}
        <h1 className="text-4xl font-bold mb-8 text-center text-red">
          Smart Home App
        </h1>

        {/* Grid layout for the sections (Sensor Data, Temperature Chart, Camera Image) */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-1">

          {/* Sensor Data Section */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            {/* Title of the sensor data section */}
            <h2 className="text-xl font-bold mb-4 text-gray-800">Sensor Status</h2>
            <ul className="list-disc pl-2">
              {/* Motion Sensor Status */}
              <li className="flex items-center justify-between py-2">
                <span className="text-gray-700">Motion:</span>
                <span
                    className={`font-bold ${motionDetected ? "text-green-500" : "text-red-500"}`}
                >
                {motionDetected ? "Detected" : "Not Detected"}
              </span>
              </li>

              {/* Door Sensor Status */}
              <li className="flex items-center justify-between py-2">
                <span className="text-gray-700">Door:</span>
                <span
                    className={`font-bold ${doorOpen ? "text-red-500" : "text-green-500"}`}
                >
                {doorOpen ? "Open" : "Closed"}
              </span>
              </li>

              {/* Window Sensor Status */}
              <li className="flex items-center justify-between py-2">
                <span className="text-gray-700">Window:</span>
                <span
                    className={`font-bold ${windowOpen ? "text-red-500" : "text-green-500"}`}
                >
                {windowOpen ? "Open" : "Closed"}
              </span>
              </li>

              {/* Temperature Sensor Status */}
              <li className="flex items-center justify-between py-2">
                <span className="text-gray-700">Temperature:</span>
                <span className="font-bold">{temperature} °C</span>
              </li>
            </ul>
          </div>

          {/* Temperature Chart Section */}
          <div className="bg-white p-6 rounded-lg shadow-md col-span-2 lg:col-span-1">
            {/* Title of the temperature chart */}
            <h2 className="text-xl font-bold mb-4 text-gray-800">
              Temperature Chart
            </h2>
            {/* The Line chart displaying the temperature data */}
            <Line data={chartData} />
          </div>

          {/* Camera Image Section */}
          <div className="bg-white p-6 rounded-lg shadow-md col-span-2 lg:col-span-1">
            {/* Title of the camera section */}
            <h2 className="text-xl font-bold mb-4 text-gray-800">Door Camera</h2>
            {/* Conditional rendering for displaying the camera image */}
            {cameraImage && showImage ? (
                <img
                    src={`data:image/jpeg;base64,${cameraImage}`} // The base64 image received from the camera
                    alt="Door Camera"
                    className="border-2 border-gray-300 rounded-lg shadow-md max-w-xs mx-auto" // Styling for the image
                />
            ) : (
                <p className="text-gray-600 text-center">No image available</p> // Fallback message when no image is available
            )}
          </div>

        </div>
      </div>
  );
}

export default App;
